package com.example.Chat_Application.control_Layer;import com.example.Chat_Application.Model_Layer.ChatMessage;import org.springframework.messaging.handler.annotation.DestinationVariable;import org.springframework.messaging.handler.annotation.MessageMapping;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class MessageController {    @MessageMapping("/chat/{to}")    public void sendMessage(@DestinationVariable String to, ChatMessage message){        System.out.println("handling:  "+ message + "to: " + to);    }}