package com.example.Chat_Application.control_Layer;import com.example.Chat_Application.Model_Layer.ChatMessage;import com.example.Chat_Application.repository_Layer.UserStorage;import org.apache.logging.log4j.message.SimpleMessage;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.messaging.handler.annotation.DestinationVariable;import org.springframework.messaging.handler.annotation.MessageMapping;import org.springframework.messaging.simp.SimpMessagingTemplate;import org.springframework.web.bind.annotation.RestController;import java.awt.event.ItemEvent;@RestControllerpublic class MessageController {    @Autowired  private SimpMessagingTemplate simplMsgTemplate;    @MessageMapping("/chat/{to}")    public void sendMessage(@DestinationVariable String to, ChatMessage message) {        System.out.println("handling:  " + message + "to: " + to);        boolean isUserExists = UserStorage.getInstance().getUsers().contains(to);        if(isUserExists){            simplMsgTemplate.convertAndSendToUser();        }    }}